class Blog{constructor(t={blogGrid:blogGrid,blogPosts:blogPosts}){this.selectors=t,this.postMaxScale=1.1,this.grid=null,this.posts=null,this.init()}init(){this.grid=document.querySelector(this.selectors.blogGrid),this.grid&&(this.posts=Array.from(this.grid.querySelectorAll(this.selectors.blogPosts)),this.initEventHandlers())}initEventHandlers(){window.addEventListener("resize",t=>{this.scalePosts()})}scalePosts(){const i=this.grid.dataset.postDefaultWidth,e=this.grid.dataset.postDefaultHeight;this.posts.forEach(t=>{let s=t.offsetWidth/i;s>this.postMaxScale&&(s=this.postMaxScale);var o=e*s;t.style.height=o+"px",t.querySelector(".blog-post-scalable").style.transform=`scale(${s})`})}}function blogInit(){var t=new Blog({blogGrid:"#blog-section.blog-grid",blogPosts:".blog-post-wrapper"});t.posts&&t.scalePosts()}export{blogInit};